import React from 'react'

import slides from './Slides';
export default class Main extends React.Component{

    constructor(props) {
        super(props);

        this.state = {
          slideNumber: 0,
          onAbout: false,
          onContact: false
        }
      }

      handleNext = () => {


        if (this.state.slideNumber == (slides.length - 1)) {
          this.setState({
            slideNumber: 0
          })
        } else {
          this.setState({
            slideNumber: this.state.slideNumber + 1
          })
        }

      }

      handleBack = () => {
        if (this.state.slideNumber == 0) {
          this.setState({
            slideNumber: slides.length - 1
          })
        } else {
          this.setState({
            slideNumber: this.state.slideNumber - 1
          })
        }
      }

    render(){
        <div className="Main">
        <div id="next" onClick={this.handleNext}>
        </div>

        <div id="back" onClick={this.handleBack} ></div>
        <div className="top-bar">

          <div className="about">about</div>
          <h1>bethany wellik</h1>
          <div className="contact"> contact</div>
        </div>

        {slides[this.state.slideNumber]}

      </div>
    }

}
