import React from 'react'

import slides from './Slides';
export default class Main extends React.Component{

    constructor(props) {
        super(props);

        this.state = {
          slideNumber: 0,
          onAbout: false,
          onContact: false
        }
      }

      handleNext = () => {


        if (this.state.slideNumber == (slides.length - 1)) {
          this.setState({
            slideNumber: 0
          })
        } else {
          this.setState({
            slideNumber: this.state.slideNumber + 1
          })
        }
        this.updateLogo();

      }

      updateLogo = () =>{
        if( this.state.slideNumber == 0 ){
            this.props.makeLogoInvisible();
        }else{
            this.props.makeLogoVisible();
        }

      }

      handleBack = () => {
        if (this.state.slideNumber == 0) {
          this.setState({
            slideNumber: slides.length - 1
          })
        } else {
          this.setState({
            slideNumber: this.state.slideNumber - 1
          })
        }
        this.updateLogo();
      }

    render(){

        return(
        <div className="Main">
        <div id="next" onClick={this.handleNext}>
        </div>

        <div id="back" onClick={this.handleBack} ></div>

        {slides[this.state.slideNumber]}

      </div>
        )
    }

}
