import React from 'react';
import './App.scss';
import {about as About} from './About'
import './Contact'
import './Main'

import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import Main from './Main';
import about from './About';



class App extends React.Component {

  constructor(props) {
    super(props)
    this.state = {
      logoVisible: false
    }
  }

  componentDidUpdate(prevProps) {
    if (this.props.location !== prevProps.location) {
      this.onRouteChanged(this.props.location);
    }
  }

  makeLogoVisible = () => {
    this.setState({
      logoVisible: true
    })
  }

  makeLogoInvisible = () => {
    this.setState({
      logoVisible: false
    })
  }

  render() {

    return (

      <Router>
        <div className="top-bar">
          <div className="about"><Link to="/about">about</Link></div>
          {!this.state.logoVisible ? "" : <h1>bethany wellik</h1>}
          <div className="contact"> contact</div>
      </div>
        <Switch>
          <Route path="/about">
            <About test={this.makeLogoVisible}></About>
          </Route>
          <Route path="/contact">
            <contact />
          </Route>
          <Route path="/">
            <Main makeLogoInvisible={this.makeLogoInvisible} makeLogoVisible={this.makeLogoVisible}></Main>
          </Route>
        </Switch>

      </Router>
    );
  }

}


export default App;
