import React from 'react';
import './App.scss';
import {About} from './About'
import './Contact'
import './Main'

import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import Main from './Main';
import about from './About';



class App extends React.Component {

  constructor(props) {
    super(props)
    this.state = {
      logoVisible: false,
      reset: false
    }
  }

  makeLogoVisible = () => {
    this.setState({
      logoVisible: true
    })
  }

  makeLogoInvisible = () => {
    this.setState({
      logoVisible: false
    })
  }

  handleAbout = () => {
    this.setState({
      reset: true
    })
  }

  render() {

    return (

      <Router>
        <div className="top-bar">
          <div className="about"><Link onClick={this.handleAbout} to="/about">about</Link></div>
          {!this.state.logoVisible ? "" : <h1><Link to={{pathname:"/home",reset:"true"}}>bethany wellik</Link></h1>}
          <div className="contact"><Link to="/contact">contact</Link></div>
      </div>
        <Switch>
          <Route path="/about">
            <About test={this.makeLogoVisible}></About>
          </Route>
          <Route path="/contact">
            <contact />
          </Route>
          <Route path="/">
            <Main reset={this.state.reset} makeLogoInvisible={this.makeLogoInvisible} makeLogoVisible={this.makeLogoVisible}></Main>
          </Route>
        </Switch>

      </Router>
    );
  }

}


export default App;
