import React from 'react'

import slides from './Slides';
export default class Main extends React.Component{

    constructor(props) {
        super(props);

        this.state = {
          slideNumber: 0,
          onAbout: false,
          onContact: false
        }
      }

      handleNext = () => {

        let newNumber = 0;
        if (this.state.slideNumber == (slides.length - 1)) {
            newNumber = 0;
        } else {
            newNumber = this.state.slideNumber + 1
        }

        this.updateLogo(newNumber);
        this.setState({
            slideNumber: newNumber
          })
      }

      updateLogo = (number) =>{
        if( number == 0 ){
            this.props.makeLogoInvisible();
        }else{
            this.props.makeLogoVisible();
        }
      }

      handleBack = () => {
        let newNumber = 0;
        if (this.state.slideNumber == 0) {
       newNumber = slides.length - 1;
        } else {

            newNumber = this.state.slideNumber - 1;

        }
        this.updateLogo(newNumber);
        this.setState({ 
            slideNumber: newNumber
          })

      }

    render(){

        return(
        <div className="Main">
        <div id="next" onClick={this.handleNext}>
        </div>

        <div id="back" onClick={this.handleBack} ></div>

        {slides[this.state.slideNumber]}

      </div>
        )
    }

}
